'use strict'

var isHex = require('./is-hex')
var tape = require('tape')

tape('truthy', function (test) {
  test.plan(4)
  test.equal(isHex('0123456789abcdef'), true)
  test.equal(isHex('0123456789ABCDEF'), true)
  test.equal(isHex('0123456789ABCDEFabcdef'), true)
  test.equal(isHex('000'), true)
})

tape('falsy', function (test) {
  test.plan(9)
  test.equal(isHex('0123456789ABCDE.Fabcdef'), false)
  test.equal(isHex('not-hex'), false)
  test.equal(isHex('z'), false)
  test.equal(isHex('089x'), false)
  test.equal(isHex([]), false)
  test.equal(isHex(true), false)
  test.equal(isHex(387560982.3), false)
  test.equal(isHex(38756098), false)
  test.equal(isHex({}), false)
})
